    console.log("click!!! ==>> " + name);
  };
  return (
    <Layout>
      <div>
        DB Test page
        <br></br>
        등록페이지...
        <Table>
          <TableHead>rr</TableHead>
          <TableBody>
            {products.map(p => {
              return (
                <img src={p.src} alt="menu" style={{ width: 250 }} onClick={handleClick(p.name)} />
              );
              {
                p.name;
              }
            })}
          </TableBody>
        </Table>
      </div>
    </Layout>
  );
};
