import React,{useState} from "react";
import socketio from "socket.io-client";
import { EditorDragHandle } from "material-ui/svg-icons";


// let num = 1;

const socket = socketio.connect("http://localhost:3001");
(() => {
    console.log("hi");
  socket.emit("joinRoom",{roomName:'myroom'});
  socket.on("recMsg", data => {
    const num2 = data.comment;
    console.log(num2);
   
  });
  
})();

const Sockettest = () => {
    const [num, setNum] = useState(0);
 
  
  const handle = (e) => {
      setNum(e.target.value)
  }

  const send = (text) => {
    socket.emit("reqMsg", {comment:text});
  }

  return (
    <div>
        <input type="text" onChange={(e) => {handle(e)}}/ >
        <button onClick={() => {send(num)}}> 보내기 </button>
      <div>{num}</div>

      <br/>
    </div>
  );
};

export default Sockettest;
