import React, { useEffect } from "react";

let onum = 101;
let data = "";
const socket = socketio.connect("http://localhost:4000");
(() => {
  socket.emit("init", { ordernum: "" });
  socket.on("welcome", msg => {
    data = msg;
  });
})();

const Waiting = ({ orderlist }) => {
  const list = data.map(num => {
    return <div>{num}</div>;
  });

  return (
    <div>
      <div>{data}</div>

      <br />
    </div>
  );
};

export default Waiting;
