import React, { useState, useEffect } from 'react'
import CheeseburgerMenu from 'cheeseburger-menu'
import HamburgerMenu from 'react-hamburger-menu'
import Admin_menu from './Admin_menu'

const contentStyles = {
    fontFamily: 'sans-serif',
    textAlign: 'center',
}

const Admin_main = () => {

    const [menuOpen] = useState(false);

    useEffect(() => {
        openMenu().then((menuOpen) => { setState(menuOpen) });
        closeMenu().then((menuOpen) => { setState(menuOpen) });
    }, []);

    return (<div>
        <br></br>
        <CheeseburgerMenu
            isOpen={state.menuOpen}
            closeCallback={closeMenu.bind(this)}>
            <Admin_menu closeCallback={closeMenu.bind(this)} />
        </CheeseburgerMenu>

        <HamburgerMenu
            isOpen={state.menuOpen}
            menuClicked={openMenu.bind(this)}
            width={50}
            height={50}
            strokeWidth={10}
            rotate={0}
            color='blue'
            borderRadius={0}
            animationDuration={0.5}
        />

        <div style={contentStyles}>
            <br></br>
            <h1>솔라시 키오스크 관리</h1>
            <p>관리자 페이지에 오신것을 환영합니다.</p>
            <br></br><br></br>
            <img src="admin_img.png" alt="admin Image"></img>
        </div>
    </div>
    );
};

export default Admin_main;
