import React, { useState } from "react";
import Layout from "../../layout/Layout";
import burger from "../../img/burger.jpg";
import chicken from "../../img/chicken.jpg";
import coke from "../../img/coke.jpg";
import set from "../../img/set.jpg";
import Table from "@material-ui/core/Table";
import TableHead from "@material-ui/core/TableHead";
import TableBody from "@material-ui/core/TableBody";
import TableRow from "@material-ui/core/TableRow";
import TableCell from "@material-ui/core/TableCell";

const defaultData = [
  { key: "4", src: set, name: "세트묵자", price: "20000", qty: 0 },
  { key: "1", src: burger, name: "햄버거임", price: "6000", qty: 0 },
  { key: "2", src: chicken, name: "치킨임", price: "17000", qty: 0 },
  { key: "3", src: coke, name: "콜라임", price: "2500", qty: 0 }
];
const TestPageForDB = props => {
  const [lists, setLists] = useState([]);
  const index = 0;
  //const [productData, setProduct] = defaultData;
  function handleClick(name) {
    console.log("CLICK! " + name);
  }

  function addProduct(p) {
    lists.push(p);
    setList(lists);
    console.log(lists);
    //getRowsData();
  }
  function getRowsData() {
    console.log("표를 만들자..");
    return (
      <div style={{}}>
        <Table style={{ width: "400px" }}>
          <TableHead>
            <TableRow>
              <TableCell>이름</TableCell>
              <TableCell>가격</TableCell>
              <TableCell>수량</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {lists.map(({ name, price, qty }) => {
              return (
                <TableRow>
                  <TableCell>{name}</TableCell>
                  <TableCell>{price}</TableCell>
                  <TableCell>{qty}</TableCell>
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </div>
    );
  }
  return (
    <Layout>
      <div>
        DB Test page
        <br></br>
        등록페이지...
        <Table>
          <TableHead>rr</TableHead>
          <TableBody>
            {defaultData.map(p => {
              return (
                <img src={p.src} alt="menu" style={{ width: 250 }} onClick={() => addProduct(p)} />
              );
              // <div>{p.name}</div>;
            })}
          </TableBody>
        </Table>
        {getRowsData()}
      </div>
    </Layout>
  );
};
// C:\Users\multicampus\git\s02p12c103\web\src\pages\test\test1_db.jsx
// C:\Users\multicampus\git\s02p12c103\web\src\img\burger.jpg
export default TestPageForDB;
