import React from "react";
import Layout from "../../layout/Layout";
import burger from "../../img/burger.jpg";
import chicken from "../../img/chicken.jpg";
import coke from "../../img/coke.jpg";
import set from "../../img/set.jpg";
import Table from "@material-ui/core/Table";
import TableHead from "@material-ui/core/TableHead";
import TableBody from "@material-ui/core/TableBody";
import TableRow from "@material-ui/core/TableRow";
import TableCell from "@material-ui/core/TableCell";

class Product extends React.Component {
  render() {
    return (
      <TableRow>
        <TableCell>
          <img src={this.props.image} alt="menu" />
          <br></br>
          {this.props.name}
        </TableCell>
      </TableRow>
    );
  }
}
const products = [
  { image: burger, name: "햄버거임" },
  { image: chicken, name: "치킨임" },
  { image: coke, name: "콜라임" },
  { image: set, name: "세트묵자" }
];
const testPageForDB = props => {
  const index = 0;
  return (
    <Layout>
      <div>
        DB Test page
        <br></br>
        등록페이지...
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>메뉴</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {products.map(p => {
              return <Product iamge={p.image} name={p.name}></Product>;
            })}
          </TableBody>
        </Table>
      </div>
    </Layout>
  );
};
// C:\Users\multicampus\git\s02p12c103\web\src\pages\test\test1_db.jsx
// C:\Users\multicampus\git\s02p12c103\web\src\img\burger.jpg
export default testPageForDB;
