    socket.emit("joinRoom",{roomName:'myroom'});
    // socket.on("recMsg", data => {
    //   num = data.comment;
    //   console.log(num);
    //   setLi(num);
    // });
    socket.emit("reqMsg", {comment:1234});
  })();

  return (
    <div>
      <div>{num}</div>

      <br />
    </div>
  );
};

export default sockettest;
